from pytest import raisesdef hello(name: str) -> str:    return f"Hello {name}"def test_hello():    assert hello('Ozod') == 'Hello Ozod'def test_random():    names = ['Jon', 'Alice', 'Michael', 'Fredo']    for name in names:        assert hello(name) == f'Hello {name}'def test_wrongs():    names = ['Jon', 'Alice', 'Michael', 'Fredo']    for name in names:        with raises(AssertionError):            assert hello(name) == f'Hello {name}!'