from threading import Threadfrom time import sleep, perf_counterdef add(x, y):    print('We are working on...')    sleep(1)    return x + ystart = perf_counter()# final1 = add(12, 12)# end = perf_counter()# print(f'Javob: {final1} Ketkazilgan vaqt {end - start :0.5f}')## start = perf_counter()# final2 = add(12, 12)t1 = Thread(target=add, args=(12, 34))t2 = Thread(target=add, args=(42, 56))t1.start()t2.start()t1.join()t2.join()end = perf_counter()print(f'Javob: Ketkazilgan vaqt {end - start :0.5f}')def write(text, filename):    with open(file=filename, mode='w+') as file:        print(f'Matnni {filename} ga yozishni boshladingiz')        sleep(2)        file.write(text)        print('Tugatdingiz')t1 = Thread(target=write, args=('world hello', 'hello.txt'))t2 = Thread(target=write, args=('salom dunyo', 'bye.txt'))t1.start()t2.start()t1.join()t2.join()from time import sleep, perf_counterfrom threading import Threaddef task():    print('Starting a task...')    sleep(1)    print('done')start_time = perf_counter()# create two new threadst1 = Thread(target=task)t2 = Thread(target=task)# start the threadst1.start()t2.start()# wait for the threads to completet1.join()t2.join()end_time = perf_counter()print(f'It took {end_time- start_time: 0.2f} second(s) to complete.')