import sysx = '''usage: calculator_cli.py [-h] num1 punc num2Bu dastur calculator vazifasini bajaradi!positional arguments:  num1       birinchi raqamini kiriting!  punc       amal ishorasini kiriting!  num2       ikkinchi raqamni kiriting!options:  -h, --help  show this help message and exitDasturni ishlatganingiz uchun mamnunmiz!'''if sys.argv[1] == '-h':    print(x)else:    try:        num1 = int(sys.argv[1])        num3 = int(sys.argv[3])        if sys.argv[2] == '+':            print(num1 + num3)        elif sys.argv[2] == '-':            print(num1 - num3)        elif sys.argv[2] == '/':            print(num1 / num3)        elif sys.argv[2] == '*':            print(num1 * num3)        elif sys.argv[2] == '%':            print(num1 % num3)        else:            print(num1 * num3)    except:        raise """Birinchi va Uchinchi paramentga integer qiymat bering! Ikkinchisiga esa arifmetik ishora ya`ni ('+', '-', '/', '*', '%')"""